def countChar (str: String) = {
    val datos = str.toList.map(c => ((),c))
    val fmap = (_ : Unit, c : Char) => List((c,1))
    val freduce = (c: Char, vs: List[Int]) => (c,vs.fold (0) (_+_))
    mapReduce (datos) (fmap) (freduce)
}

