def countCharFile (filePath: String) = {

    import scala.io.Source

    import collection.mutable

    val lines : List[String] = Source.fromFile(filePath).getLines.toList
    val datos = lines.map(l => ((),l))
    val fmap = (_ : Unit, l : String) => {
        val mlist : mutable.ListBuffer[(Char, Int)] = mutable.ListBuffer()
        for (c <- l) {
            mlist ++= List((c,1))
        }
        val list = List() ++ mlist
        list
    }
    val freduce = (c: Char, vs: List[Int]) => (c,vs.fold (0) (_+_))
    mapReduce (datos) (fmap) (freduce)
}

countCharFile("doc/ejemplo.txt")

def wordCount (filePath: String) = {

import scala.io.Source
import collection.mutable

    val lines : List[String] = Source.fromFile(filePath).getLines.toList
    val datos = lines.map(l => ((),l))

    val fmap = (_ : Unit, l : String) => {
        val mlist : mutable.ListBuffer[(String, Int)] = mutable.ListBuffer()
        for (w <- l.split(" ")) {
            if (!w.isEmpty()) {
                mlist ++= List((w,1))
            }
        }
        val list = List() ++ mlist
        list
    }
    val freduce = (w: String, vs: List[Int]) => (w,vs.fold (0) (_+_))

    mapReduce (datos) (fmap) (freduce)
}

