def promedio (nums: List[Double]) : Double = {

    val numbers = nums.map(n => ((),n))

    val fmap = (_ : Unit, number : Double) =>  List(((),number))
    val freduce = (_ : Unit, numbers: List[Double]) => {
        var cantidad = 0
        var sum : Double = 0
        for (n <- numbers) {
            sum += n
            cantidad += 1
        }
        val suma = (sum, cantidad)
        suma
    }
    val suma = mapReduce (numbers) (fmap) (freduce)
    val res = suma(0)
    return res._1/res._2
}

